import { GET_LOGIN_REQUESTED, GET_DATA_REQUESTED, GET_SHOW_POST_REQUESTED,GET_DELETE_REQUESTED } from "../constants"
export const getdata = (data) =>{
    return({
        type:GET_LOGIN_REQUESTED,
        payload:data
    })
}

export const showdata = () =>{
    return({
        type:GET_DATA_REQUESTED
    })
}

export const addPost = (data) =>{
    // console.log("action page invoked",data)
    return({
        type:GET_SHOW_POST_REQUESTED,
        payload:data
    })
}

export const deletePost = (data) =>{
    // console.log("action page invoked",data)
    return({
        type:GET_DELETE_REQUESTED,
        payload:data
    })
}
