import React from 'react'
import { useDispatch,useSelector } from 'react-redux'
import { useForm } from "react-hook-form";
import { getdata } from '../actions/action';

export default function Login() {
  const dispatch = useDispatch(); 
  const { register, handleSubmit, watch, formState: { errors } } = useForm();
  const onSubmit = data => dispatch(getdata(data));

  return (
    /* "handleSubmit" will validate your inputs before invoking "onSubmit" */
    <form onSubmit={handleSubmit(onSubmit)}>
      {/* register your input into the hook by invoking the "register" function */}
      <input defaultValue="test" {...register("email", { required: true })} />
      {errors.email && <span style={{color:"red"}}>This email is required</span>}
      {/* include validation with required or other standard HTML validation rules */}
      <input {...register("password", { required: true })} />
      {/* errors will return when field validation fails  */}
      {errors.password && <span style={{color:"red"}}>This password is required</span>}

      <input type="submit" />
    </form>
  );
}
