import { GET_LOGIN_SUCCESS,GET_LOGIN_FAILURE } from "../constants"
const initialState = {
    user:[]
}

export const LoginReducer = (state=initialState,action) => {

    switch(action.type)
    {
        case GET_LOGIN_SUCCESS:
            return ({
                ...state,
                user:action.user
            })

        case GET_LOGIN_FAILURE:
            return ({
                ...state,
            })
    }
}
