import Api from "./Api";
import {GET_LOGIN_FAILURE,GET_LOGIN_REQUESTED,GET_LOGIN_SUCCESS} from "../constants";
import { call, put, takeEvery } from 'redux-saga/effects'

function* fetchUser(action)
{
  try {
  const responce = yield call(Api,action.payload);
  localStorage.setItem('user-info',JSON.stringify(responce.data));
  yield put({type:GET_LOGIN_SUCCESS, user:responce.data }) 
  }

  catch(e)
  {
 yield put ({ type:GET_LOGIN_FAILURE,error:e.message});
  }
}

function* mySaga() {
  yield takeEvery(GET_LOGIN_REQUESTED, fetchUser);
}


export default mySaga;
